import axios from 'axios';

const inputContainer = document.getElementById("inputContainer");
const loadingContainer = document.getElementById("loadingContainer");
const messagesContainer = document.getElementById("messagesContainer");
const userList = document.getElementById("userList");
const messageList = document.getElementById("messageList");
const backBtn = document.getElementById("backBtn");

document.getElementById("submitBtn").onclick = async () => {
    const userId = document.getElementById("userId").value;
    const dataPath = document.getElementById("dataPath").value;

    inputContainer.classList.add("is-hidden");
    loadingContainer.classList.remove("is-hidden");

    try {
        const response = await axios.post("/api/messages", { data_path: dataPath }, { params: { user_id: userId } });
        const messages = response.data;

        const users = {};

        messages.forEach(message => {
            if (!users[message.author]) {
                users[message.author] = [];
            }
            users[message.author].push(message);
        });

        Object.entries(users).forEach(([username, userMessages]) => {
            const userBubble = document.createElement("div");
            userBubble.classList.add("column", "is-one-quarter", "user-bubble");
            userBubble.textContent = username;
            userBubble.onclick = () => {
                displayMessages(userMessages);
            };
            userList.appendChild(userBubble);
        });

        loadingContainer.classList.add("is-hidden");
        messagesContainer.classList.remove("is-hidden");
    } catch (err) {
        console.error(err);
        alert("An error occurred while processing your request.");
        inputContainer.classList.remove("is-hidden");
        loadingContainer.classList.add("is-hidden");
    }
};

function displayMessages(messages) {
    userList.classList.add("is-hidden");
    messageList.innerHTML = '';

    messages.forEach(message => {
        const messageElem = document.createElement("div");
        messageElem.classList.add("message");
        messageElem.textContent = message.content;
        messageList.appendChild(messageElem);
    });

    messageList.classList.remove("is-hidden");
    backBtn.classList.remove("is-hidden");

    backBtn.onclick = () => {
        messageList.classList.add("is-hidden");
        backBtn.classList.add("is-hidden");
        userList.classList.remove("is-hidden");
    };
}
