import React, { useState } from 'react';
import {
  Container,
  Row,
  Col,
  Button,
  InputGroup,
  FormControl,
  ListGroup,
  Spinner,
  Form,
} from 'react-bootstrap';
import axios from 'axios';
import './App.css';

function App() {
  const [dataPath, setDataPath] = useState('');
  const [userId, setUserId] = useState('');
  const [messages, setMessages] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const fetchData = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:8000/api/messages', {
        data_path: dataPath,
        user_id: userId,
      });
      setMessages(response.data);
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setIsLoading(false);
    }
  };

  const displayUserMessages = (username) => {
    setSelectedUser(username);
  };

  const displayedMessages = messages.filter(
    (message) => message.username === selectedUser
  );

  const uniqueUsers = Array.from(new Set(messages.map((message) => message.username)));

  return (
    <div className="App">
      <Container fluid>
        {selectedUser ? (
          <Row className="my-4">
            <Col>
              <Button onClick={() => setSelectedUser('')}>&lt; Back</Button>
            </Col>
          </Row>
        ) : (
          <Row className="my-4">
            <Col>
              <Form onSubmit={fetchData}>
                <InputGroup>
                  <FormControl
                    placeholder="Enter the path to your Discord data package"
                    value={dataPath}
                    onChange={(e) => setDataPath(e.target.value)}
                  />
                  <FormControl
                    placeholder="Enter your user ID"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                  />
                  <Button variant="primary" type="submit">
                    Load Messages
                  </Button>
                </InputGroup>
              </Form>
            </Col>
          </Row>
        )}

        {isLoading ? (
          <Row className="my-4">
            <Col className="text-center">
              <Spinner animation="border" role="status">
                <span className="sr-only">Loading...</span>
              </Spinner>
            </Col>
          </Row>
        ) : selectedUser ? (
          <Row>
            <Col>
              <ListGroup>
                {displayedMessages.map((message, index) => (
                  <ListGroup.Item key={index}>{message.content}</ListGroup.Item>
                ))}
              </ListGroup>
            </Col>
          </Row>
        ) : (
          <Row>
            {uniqueUsers.map((username, index) => (
              <Col key={index} xs={6} md={4} lg={3} className="mb-4">
                <Button
                  variant="outline-primary"
                  className="w-100"
                  onClick={() => displayUserMessages(username)}
                >
                  {username}
                </Button>
              </Col>
            ))}
          </Row>
        )}
      </Container>
    </div>
  );
}

export default App;
