import React, { useState } from 'react';
import { Container, Row, Col, Button, InputGroup, FormControl, ListGroup } from 'react-bootstrap';
import axios from 'axios';
import './App.css';

function App() {
  const [dataPath, setDataPath] = useState('');
  const [messages, setMessages] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/messages', { data_path: dataPath });
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="App">
      <Container fluid>
        <Row className="my-4">
          <Col>
            <InputGroup>
              <FormControl
                placeholder="Enter the path to your Discord data package"
                value={dataPath}
                onChange={(e) => setDataPath(e.target.value)}
              />
              <Button variant="primary" onClick={fetchData}>
                Load Messages
              </Button>
            </InputGroup>
          </Col>
        </Row>
        <Row>
          <Col>
            <ListGroup>
              {messages.map((message, index) => (
                <ListGroup.Item key={index}>
                  <strong>{message.username}</strong> ({message.channel_id}): {message.content}
                </ListGroup.Item>
              ))}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
